import { useEffect, useState, useRef } from "react";
import OutputNode from "./outputNode";

const Patchbay = () => {
  const svgRef = useRef(null);

  const outs = 8;
  const ins = 24;

  const [points, setPoints] = useState([
    { x: 30, y: 30 },
    { x: 30, y: 130 },
    { x: 600, y: 130 },
    { x: 600, y: 230 },
  ]);

  const startDrag = (event, index) => {
    event.preventDefault();

    const mousemove = (event) => {
      event.preventDefault();
      let cursorPoint = svgRef.current.createSVGPoint();
      cursorPoint.x = event.clientX;
      cursorPoint.y = event.clientY;
      cursorPoint = cursorPoint.matrixTransform(
        svgRef.current.getScreenCTM().inverse()
      );
      setPoints(
        points.map((p, i) =>
          index === i
            ? {
                x: Math.max(Math.min(cursorPoint.x, 1000), 0),
                y: Math.max(Math.min(cursorPoint.y, 1000), 0),
              }
            : p
        )
      );
    };

    const mouseup = (event) => {
      document.removeEventListener("mousemove", mousemove);
      document.removeEventListener("mouseup", mouseup);
    };

    document.addEventListener("mousemove", mousemove);
    document.addEventListener("mouseup", mouseup);
  };

  return (
    <div className="h-full w-full bg-slate-300">
      <svg viewBox="0 0 1000 1000" ref={svgRef}>
        {[...Array(outs)].map((n, i) => (
          <OutputNode i={i} />
        ))}
        <path
          d={`M ${points[0].x} ${points[0].y} C ${points[0].x} ${
            points[0].y + 100
          }, ${points[2].x} ${points[2].y}, ${points[3].x} ${points[3].y}`}
          fill="transparent"
          stroke="cornflowerblue"
          strokeWidth="6"
        />
        {/* <path
          d={`M ${points[0].x} ${points[0].y} L ${points[1].x} ${points[1].y} M ${points[2].x} ${points[2].y} L ${points[3].x} ${points[3].y}`}
          fill="transparent"
          stroke="gray"
          strokeWidth="2"
        /> */}
        {points.map((point, i) => (
          <g transform="translate(-15, -15)">
            <rect
              x={point.x}
              y={point.y}
              key={i}
              width="30"
              height="30"
              onMouseDown={(e) => startDrag(e, i)}
            />
          </g>
        ))}
      </svg>
    </div>
  );
};

export default Patchbay;
