import { useEffect, useState, useRef } from "react";
import OutputNode from "./outputNode";
import InputNode from "./inputNode";
import PatchCable from "./patchCable";

const Patchbay = () => {
  const svgRef = useRef(null);

  const [patchCables, setPatchCables] = useState([]);

  const outs = 8;
  const ins = 8;

  const createPatchCable = (startPt) => {
    console.log("creating patch cable at: ");
    console.log(startPt);
    setPatchCables((prevCables) => [...prevCables, { startPt }]);
  };

  return (
    <div className="h-[300px] w-[700px] bg-slate-300">
      <svg viewBox="0 0 700 300">
        {[...Array(ins)].map((n, i) => (
          <InputNode key={`input-${i}`} i={i} />
        ))}
        {[...Array(outs)].map((n, i) => (
          <OutputNode
            key={`output-${i}`}
            i={i}
            createPatchCable={createPatchCable}
          />
        ))}
        {patchCables.map((cable, index) => (
          <PatchCable key={`cable-${index}`} startPt={cable.startPt} />
        ))}
        {/* <PatchCable key={`cable-${index}`} startPt={{ x: 77.7, y: 300 }} /> */}
      </svg>
    </div>
  );
};

export default Patchbay;
