import { useEffect, useState, useRef } from "react";
import OutputNode from "./outputNode";
import InputNode from "./inputNode";
import PatchCable from "./patchCable";

const Patchbay = () => {
  const svgRef = useRef(null);

  const [patchCables, setPatchCables] = useState([]);

  const outs = 8;
  const ins = 8;

  const createPatchCable = (startPt) => {
    setPatchCables((prevCables) => [...prevCables, { startPt }]);
    console.log(patchCables);
  };

  const removePatchCable = (id) => {
    console.log("removing patch cable at: " + id);
    console.log(patchCables);
    //remove patch cable at id
    setPatchCables((prevCables) => prevCables.filter((cable, i) => i !== id));

    // console.log(patchCables.filter((cable, i) => i !== id));
    // setPatchCables((prevCables) => prevCables.filter((cable, i) => i !== id));
    // console.log(patchCables);
  };

  return (
    <div className="h-[300px] w-[700px] bg-slate-300">
      <svg viewBox="0 0 700 300">
        {patchCables.map((cable, i) => (
          <PatchCable
            key={`cable-${i}`}
            id={i}
            startPt={cable.startPt}
            handleRemovePatchCable={removePatchCable}
            onMouseDown={() => console.log("hovering over cable")}
          />
        ))}
        {[...Array(ins)].map((node, i) => (
          <InputNode key={`input-${i}`} i={i} />
        ))}
        {[...Array(outs)].map((node, i) => (
          <OutputNode
            key={`output-${i}`}
            i={i}
            handleCreatePatchCable={createPatchCable}
          />
        ))}
      </svg>
    </div>
  );
};

export default Patchbay;
