import React, { useState, useRef } from "react";

const PatchCable = ({ i, startPt }) => {
  // const [hover, setHover] = useState(false);
  const svgRef = useRef(null);

  const [points, setPoints] = useState([
    { x: startPt.x, y: startPt.y },
    { x: 600, y: 130 },
  ]);

  const startDrag = (event, index) => {
    event.preventDefault();
    const el = event.target;
    el.classList.add("pointer-events-none");
    console.log(event.target.classList);

    const mousemove = (event) => {
      event.preventDefault();
      let cursorPoint = svgRef.current.createSVGPoint();
      cursorPoint.x = event.clientX;
      cursorPoint.y = event.clientY;
      cursorPoint = cursorPoint.matrixTransform(
        svgRef.current.getScreenCTM().inverse()
      );
      setPoints(
        points.map((p, i) =>
          index === i
            ? {
                x: Math.max(Math.min(cursorPoint.x, 1000), 0),
                y: Math.max(Math.min(cursorPoint.y, 1000), 0),
              }
            : p
        )
      );
    };

    const mouseup = (event) => {
      if (event.target.id.includes("inNode")) {
        console.log("connected to input node at:");
        console.log(
          event.target.attributes.cx.value +
            " x " +
            event.target.attributes.cy.value
        );
        setPoints(
          points.map((p, i) =>
            index === i
              ? {
                  x: event.target.attributes.cx.value,
                  y: event.target.attributes.cy.value,
                }
              : p
          )
        );
      } else {
        console.log("not connected to input node");
        //remove the cable
      }

      el.classList.remove("pointer-events-none");
      document.removeEventListener("mousemove", mousemove);
      document.removeEventListener("mouseup", mouseup);
    };

    document.addEventListener("mousemove", mousemove);
    document.addEventListener("mouseup", mouseup);
  };

  return (
    <svg ref={svgRef}>
      <path
        className="pointer-events-none"
        d={`M ${points[0].x} ${points[0].y} C ${points[0].x} ${
          points[0].y - (points[0].y - points[1].y) / 2
        }, ${points[1].x} ${points[1].y + (points[0].y - points[1].y) / 2}, ${
          points[1].x
        } ${points[1].y}`}
        fill="transparent"
        stroke="cornflowerblue"
        strokeWidth="6"
      />
      {points.map((point, i) => (
        <circle
          cx={point.x}
          cy={point.y}
          r="8"
          fill="cornflowerblue"
          onMouseDown={(e) => startDrag(e, i)}
        />
      ))}
    </svg>
  );
};

export default PatchCable;
